import React, { useEffect, useState } from 'react';
import axios from 'axios';

function ItemsDisplay(props) {

    const printItems = (props) => {
        const { items } = props;

        if (items.length > 0) {
            return (
                items.map((item, index) => {
                    console.log(item);

                    // Add create, delete and update buttons here
                    // "gridify" the output here
                    return (
                        <div class="row">
                            <div class="col-sm-4" className="item" key={item.id}>
                                <div className='item' key={item.id}>
                                    <h3 className="item_title">Item name: {item.title}</h3>
                                    <p className="item_qty">Quantity: {item.quantity}</p>
                                    <p className="item_desc">Description {item.description}</p>
                                </div>
                            </div>

                            <div class="col-sm-8" className="buttons">
                                <div class="row">
                                    <div class="col-sm-4">
                                        <button type="button" class="btn btn-primary">Primary</button>
                                    </div>

                                    <div class="col-sm-4">
                                        <button type="button" class="btn btn-primary">Primary</button>
                                    </div>

                                    <div class="col-sm-4">
                                        <button type="button" class="btn btn-primary">Primary</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    )
                })
            )
        } else {
            return (<h3>No items have been created yet, click <a href="/create">here</a> to create some!</h3>)
        }
    }

    return (
        <>
            {printItems(props)}
        </>
    )
}

export default function ReadItems() {

    const [items, getItems] = useState('');

    const url = "http://localhost:8000/";

    useEffect(() => {
        getAllItems();
    }, []);
    const getAllItems = () => {
        axios.get(`${url}items`)
            .then((response) => {
                const items = response.data
                getItems(items);
            })
            .catch(error => console.error(`Logged error: ${error}`));
    }

    return (<ItemsDisplay items={items} />)
}
