import { useLocation, Navigate } from 'react-router-dom'
import axios from 'axios';

function UpdateItem(id, name, desc, qty) {

    const url = "http://localhost:8000/";

    console.log("Updated")

    let request = {
        "title": name,
        "quantity": qty,
        "description": desc
    };

    console.log("Request to be sent", request);

    axios.put(`${url}items/${id}`, request
    ).then((response) => {
        console.log(response)
    }).catch(error => console.error(`Logged error: ${error}`));
}

function UpdateItemForm() {

    const location = useLocation()

    // If we are not redirected from the main page
    // or if the state variable no longer exists
    // we return to the homepage
    if (location.state === null) {
        console.log("Yarrrredirected");
        return <Navigate to="/" />
    }

    const from = location.state;
    console.log("Location state: ", from);

    //const [ title, setTitle ] = useState("");
    //const [ desc, setDesc ] = useState("");
    //const [ qty, setQty ] = useState("");

    // Get the values of the state variable
    const item_id = from.id
    let title = from.title;

    console.log("Item id", item_id);
    console.log("Title", title);
    let desc = from.description;
    let qty = from.quantity;

    const handleSubmit = () => {
        UpdateItem(item_id, title, desc, qty);
    }

    return (
        <div class="container">
            <h2>Update Item</h2>

            <form onSubmit={handleSubmit}>
                <div class="form-group">
                    <label for="Title">Title</label>
                    <input type="email" class="form-control" id="exampleFormControlInput1" placeholder={title} 
                    onChange = {(e) => {title = e.target.value}}/>
                </div>
                <div class="form-group">
                    <label for="exampleFormControlSelect1">Quantity</label>
                    <select class="form-control" id="exampleFormControlSelect1" placeholder={qty}
                     onChange = {(e) => {qty = e.target.value}}>
                        <option>1</option>
                        <option>2</option>
                        <option>3</option>
                        <option>4</option>
                        <option>5</option>
                    </select>
                </div>
                <div class="form-group">
                    <label for="exampleFormControlTextarea1">Description</label>
                    <textarea class="form-control" id="exampleFormControlTextarea1" rows="3" placeholder={desc}
                    onChange = {(e) => {desc = e.target.value}}></textarea>
                </div>
            </form>

            <input type="submit" value="Submit" />
        </div>

    )
}


/*

                <div class="form-group">
                    <label for="exampleFormControlSelect2">Example multiple select</label>
                    <select multiple class="form-control" id="exampleFormControlSelect2">
                        <option>1</option>
                        <option>2</option>
                        <option>3</option>
                        <option>4</option>
                        <option>5</option>
                    </select>
                </div>

*/

export default UpdateItemForm;
