import { useLocation, Navigate } from 'react-router-dom'
import { useState } from 'react'
import axios from 'axios';

function UpdateItem(id, name, desc, qty) {

    const url = "http://localhost:8000/";

    const updateSpecificItem = () => {
        axios.put(`${url}items/${id}`, {
            "title": name,
            "quantity": qty,
            "description": desc
        }).then((response) => {
            console.log(response)
        }).catch(error => console.error(`Logged error: ${error}`));
    }

    console.log("Updated")
    updateSpecificItem();
}

function UpdateItemForm() {

    const location = useLocation()

    // If we are not redirected from the main page
    /*
    if (location.state === null) {
        console.log("Yarrrredirected");
        return <Navigate to = "/"/>
    }
    */

    const { from } = location.state;

    //const [ title, setTitle ] = useState("");
    //const [ desc, setDesc ] = useState("");
    //const [ qty, setQty ] = useState("");

    // Get the values of the state variable
    const item_id = location.state.id
    let title = location.state.title;
    let desc = location.state.description;
    let qty = location.state.qty;

    console.log(location.state)

    const handleSubmit = (event) => {
        UpdateItem(item_id, title, desc, qty)
    }

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Description:
                <textarea onChange={(e) => {
                    title = e}}/>
            </label>
            <input type="submit" value="Submit"/>
        </form>
    )
}

export default UpdateItemForm;
