import { Navigate } from 'react-router-dom';
import axios from 'axios';


function CreateItem(name, desc, qty) {

    const url = "http://localhost:8000/";

    let request = {
        "title": name,
        "quantity": qty,
        "description": desc
    };

    axios.post(`${url}items/`, request
    ).then((response) => {
        console.log(response)
    }).catch(error => console.error(`Logged error: ${error}`));
}

function CreateItemForm() {

    // If we are not redirected from the main page
    // or if the state variable no longer exists
    // we return to the homepage

    // Get the values of the state variable
    let title = "";
    let desc = "";
    let qty = 0;

    const handleSubmit = () => {
        CreateItem(title, desc, qty);

        return <Navigate to="/" />
    }

    return (
        <div class="container">
            <h2>Update Item</h2>

            <form onSubmit={handleSubmit}>
                <div class="form-group">
                    <label for="Title">Title</label>
                    <input class="form-control" id="exampleFormControlInput1" placeholder={title} 
                    onChange = {(e) => {title = e.target.value}}/>
                </div>
                <div class="form-group">
                    <label for="exampleFormControlSelect1">Quantity</label>
                    <select class="form-control" id="exampleFormControlSelect1" placeholder={qty}
                     onChange = {(e) => {qty = e.target.value}}>
                        <option>1</option>
                        <option>2</option>
                        <option>3</option>
                        <option>4</option>
                        <option>5</option>
                    </select>
                </div>
                <div class="form-group">
                    <label for="exampleFormControlTextarea1">Description</label>
                    <textarea class="form-control" id="exampleFormControlTextarea1" rows="3" placeholder={desc}
                    onChange = {(e) => {desc = e.target.value}}></textarea>
                </div>

                <input type="submit" value="Submit" />
            </form>
        </div>
    )
}

export default CreateItemForm;
